import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  PieChart, Pie, Cell, ResponsiveContainer
} from "recharts";
import API_BASE_URL from "../config";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [income, setIncome] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      const [expenseRes, incomeRes] = await Promise.all([
        axios.get(`${API_BASE_URL}/finance/expenses`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get(`${API_BASE_URL}/finance/income`, {
          headers: { Authorization: `Bearer ${token}` },
        })
      ]);

      const sortedExpenses = expenseRes.data.sort((a, b) => new Date(b.date) - new Date(a.date));
      setExpenses(sortedExpenses);
      setTotalExpenses(sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0));

      const categoryMap = {};
      sortedExpenses.forEach(expense => {
        categoryMap[expense.category] = (categoryMap[expense.category] || 0) + expense.amount;
      });

      setCategoryTotals(Object.entries(categoryMap).map(([name, value]) => ({
        name,
        value: parseFloat(value.toFixed(2))
      })));

      setIncome(incomeRes.data);
      setTotalIncome(incomeRes.data.reduce((sum, income) => sum + income.amount, 0));

      setLoading(false);
    } catch (err) {
      console.error("Error fetching data:", err);
      setError("Failed to fetch financial data.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  const formatCurrency = (amount) => new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(amount);

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 p-6 bg-gray-100 min-h-screen overflow-x-hidden">
        <div className="max-w-7xl mx-auto">

          {/* Greeting Card */}
          <div className="bg-blue-500 text-white p-6 rounded-lg flex items-center justify-between mb-6 shadow-lg">
            <h2 className="text-2xl font-bold">Hello, Demmy! ðŸ‘‹</h2>
            <img src="/path/to/avatar.png" alt="User Avatar" className="w-12 h-12 rounded-full" />
          </div>

          {/* Overview Cards */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
            {[
              { title: "Total Expenses", value: formatCurrency(totalExpenses), color: "red" },
              { title: "Total Income", value: formatCurrency(totalIncome), color: "green" },
              { title: "Balance", value: formatCurrency(totalIncome - totalExpenses), color: "blue" }
            ].map((card, index) => (
              <div key={index} className={`bg-white rounded-lg shadow-lg p-6 border-t-4 border-${card.color}-500`}>
                <h3 className="text-lg font-semibold text-gray-600">{card.title}</h3>
                <p className={`text-3xl font-bold text-${card.color}-600`}>{card.value}</p>
              </div>
            ))}
          </div>

          {/* Charts Section */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Expense Breakdown</h3>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={categoryTotals} cx="50%" cy="50%" outerRadius={80} fill="#8884d8" dataKey="value">
                    {categoryTotals.map((entry, index) => <Cell key={index} fill={COLORS[index % COLORS.length]} />)}
                  </Pie>
                  <Tooltip formatter={(value) => formatCurrency(value)} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="bg-white rounded-lg shadow-lg p-6">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Monthly Expenses</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={monthlyExpenses}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis tickFormatter={(value) => formatCurrency(value)} />
                  <Tooltip formatter={(value) => formatCurrency(value)} />
                  <Legend />
                  <Bar dataKey="amount" fill="#8884d8" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Transactions Table */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Recent Transactions</h3>
            <div className="overflow-x-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="bg-gray-200 text-gray-600 uppercase text-sm">
                    <th className="py-3 px-6 text-left">Category</th>
                    <th className="py-3 px-6 text-left">Amount</th>
                    <th className="py-3 px-6 text-left">Date</th>
                  </tr>
                </thead>
                <tbody>
                  {expenses.slice(0, 5).map((expense) => (
                    <tr key={expense.id} className="border-b">
                      <td className="py-3 px-6">{expense.category}</td>
                      <td className="py-3 px-6 text-red-600">{formatCurrency(expense.amount)}</td>
                      <td className="py-3 px-6">{new Date(expense.date).toLocaleDateString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Add Expense Button */}
          <div className="text-center mt-6">
            <button onClick={() => navigate("/add-expense")} className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 shadow-md">
              Add Expense
            </button>
          </div>

        </div>
      </div>
    </div>
  );
};

export default Dashboard;
