import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { API_BASE_URL } from "../config";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

const Dashboard = () => {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchExpenses = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/login");
          return;
        }
        const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Sort expenses by date (newest first)
        const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
        setExpenses(sortedExpenses);

        // Calculate total expenses
        const total = sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0);
        setTotalExpenses(total);

        // Compute category totals
        const categoryMap = {};
        sortedExpenses.forEach(expense => {
          categoryMap[expense.category] = (categoryMap[expense.category] || 0) + expense.amount;
        });
        setCategoryTotals(
          Object.entries(categoryMap).map(([name, value]) => ({ name, value: parseFloat(value.toFixed(2)) }))
        );

        // Compute monthly totals
        const monthlyMap = {};
        sortedExpenses.forEach(expense => {
          const month = new Date(expense.date).toLocaleString("en-US", { month: "long" });
          monthlyMap[month] = (monthlyMap[month] || 0) + expense.amount;
        });

        const monthOrder = [
          "January", "February", "March", "April", "May", "June",
          "July", "August", "September", "October", "November", "December"
        ];

        setMonthlyData(
          Object.entries(monthlyMap)
            .map(([month, amount]) => ({ month, amount: parseFloat(amount.toFixed(2)) }))
            .sort((a, b) => monthOrder.indexOf(a.month) - monthOrder.indexOf(b.month))
        );

      } catch (error) {
        console.error("Error fetching expenses:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchExpenses();
  }, []);

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <p>Total Expenses: â‚¹{totalExpenses.toFixed(2)}</p>

          {/* Bar Chart for Category Totals */}
          <h3>Expenses by Category</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={categoryTotals}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#82ca9d" />
            </BarChart>
          </ResponsiveContainer>

          {/* Bar Chart for Monthly Expenses */}
          <h3>Monthly Expenses</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={monthlyData}>
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="amount" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </>
      )}
    </div>
  );
};

export default Dashboard;
