import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, LineChart, Line,
  PieChart, Pie, Cell, ResponsiveContainer, Legend
} from "recharts";
import API_BASE_URL from "../config";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

// Colors for charts
const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
      setExpenses(sortedExpenses);

      const total = sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0);
      setTotalExpenses(total);

      const categoryMap = {};
      sortedExpenses.forEach(expense => {
        categoryMap[expense.category] = (categoryMap[expense.category] || 0) + expense.amount;
      });

      const categoryData = Object.entries(categoryMap).map(([name, value]) => ({
        name,
        value: parseFloat(value.toFixed(2))
      }));
      setCategoryTotals(categoryData);

      const monthlyMap = {};
      sortedExpenses.forEach(expense => {
        const month = new Date(expense.date).toLocaleString('default', { month: 'long' });
        monthlyMap[month] = (monthlyMap[month] || 0) + expense.amount;
      });

      const monthlyData = Object.entries(monthlyMap).map(([month, amount]) => ({
        month,
        amount: parseFloat(amount.toFixed(2))
      }));
      setMonthlyExpenses(monthlyData);

      setLoading(false);
    } catch (err) {
      console.error("Error fetching expenses:", err);
      setError(err.response?.data?.message || "Failed to fetch expenses");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  if (loading) {
    return (
      <div className="flex">
        <Sidebar />
        <div className="flex-1 flex justify-center items-center min-h-screen">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading your financial data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        {/* Header */}
        <div className="dashboard-header">
          <h1>Hi, <span className="font-bold">Prajwal</span></h1>
          <p>Here's your latest financial summary.</p>
          <div className="dashboard-icons">
            <button className="notification-icon">ðŸ””</button>
            <img src="/profile.jpg" alt="User Profile" className="profile-avatar" />
          </div>
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          {[
            { title: "Balance", value: `$${totalExpenses}`, color: "blue" },
            { title: "Revenue", value: "$2500.09", color: "yellow" },
            { title: "Sales", value: "$1509", color: "red" }
          ].map((card, index) => (
            <div key={index} className={`stat-card bg-${card.color}`}>
              <h3>{card.title}</h3>
              <p>{card.value}</p>
            </div>
          ))}
        </div>

        {/* Charts Section */}
        <div className="charts-container">
          <div className="chart-box">
            <h3>Expense Trends</h3>
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={monthlyExpenses}>
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip />
                <Line type="monotone" dataKey="amount" stroke="#8884d8" strokeWidth={3} />
              </LineChart>
            </ResponsiveContainer>
          </div>

          <div className="chart-box">
            <h3>Expense Categories</h3>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie data={categoryTotals} cx="50%" cy="50%" outerRadius={80} fill="#8884d8" dataKey="value">
                  {categoryTotals.map((entry, index) => (
                    <Cell key={index} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Budget Goals */}
        <div className="goal-section">
          <div className="goal-box">
            <h3>Business Funding</h3>
            <div className="progress-circle" style={{ background: `conic-gradient(#4CAF50 80%, #ddd 20%)` }}>
              <span>80%</span>
            </div>
          </div>

          <div className="goal-box">
            <h3>Top Up Balance</h3>
            <div className="progress-circle" style={{ background: `conic-gradient(#FF5733 70%, #ddd 30%)` }}>
              <span>70%</span>
            </div>
          </div>
        </div>

        {/* Add Expense Button */}
        <div className="add-expense-btn">
          <button onClick={() => navigate("/add-expense")} className="primary-btn">
            + Add Expense
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
