import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  PieChart, Pie, Cell, LineChart, Line, BarChart, Bar, 
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  AreaChart, Area, RadialBarChart, RadialBar, ScatterChart, Scatter
} from "recharts";
import API_BASE_URL from "../config";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#8884d8", "#82ca9d", "#FF6B6B", "#4ECDC4"];

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [categoryTotals, setCategoryTotals] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [dailyExpenses, setDailyExpenses] = useState([]);
  const [paymentMethodData, setPaymentMethodData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  const userName = localStorage.getItem("userName") || "User";

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/login");
          return;
        }

        const response = await axios.get(`${API_BASE_URL}/finance/expenses`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const sortedExpenses = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
        setExpenses(sortedExpenses);

        // Calculate total expenses
        setTotalExpenses(sortedExpenses.reduce((sum, expense) => sum + expense.amount, 0));

        // Calculate category totals
        const categoryData = sortedExpenses.reduce((acc, expense) => {
          acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
          return acc;
        }, {});
        setCategoryTotals(Object.entries(categoryData).map(([name, value]) => ({ name, value })));

        // Calculate monthly expenses
        const monthlyData = sortedExpenses.reduce((acc, expense) => {
          const month = new Date(expense.date).toLocaleString("default", { month: "short" });
          acc[month] = (acc[month] || 0) + expense.amount;
          return acc;
        }, {});
        setMonthlyExpenses(Object.entries(monthlyData).map(([month, amount]) => ({ month, amount })));

        // Calculate daily expenses (last 7 days)
        const last7Days = [...Array(7)].map((_, i) => {
          const d = new Date();
          d.setDate(d.getDate() - i);
          return d.toLocaleDateString("en-US", { weekday: 'short' });
        }).reverse();

        const dailyData = last7Days.map(day => {
          const dayExpenses = sortedExpenses.filter(exp => 
            new Date(exp.date).toLocaleDateString("en-US", { weekday: 'short' }) === day
          );
          const total = dayExpenses.reduce((sum, exp) => sum + exp.amount, 0);
          return { day, amount: total };
        });
        setDailyExpenses(dailyData);

        // Calculate payment method distribution
        const paymentData = sortedExpenses.reduce((acc, expense) => {
          if (expense.paymentMethod) {
            acc[expense.paymentMethod] = (acc[expense.paymentMethod] || 0) + expense.amount;
          }
          return acc;
        }, {});
        setPaymentMethodData(Object.entries(paymentData).map(([name, value]) => ({ name, value })));

        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch expenses");
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [navigate]);

  const formatCurrency = (amount) => new Intl.NumberFormat("en-US", { style: "currency", currency: "USD" }).format(amount);

  const toggleChat = () => setChatOpen(!chatOpen);

  const handleMessageSend = async () => {
    if (newMessage.trim() === "") return;

    setMessages((prevMessages) => [
      ...prevMessages,
      { sender: "user", text: newMessage },
    ]);

    try {
      const response = await axios.post(
        "https://api.openai.com/v1/completions",
        {
          model: "gpt-3.5-turbo",
          prompt: newMessage,
          max_tokens: 150,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer YOUR_OPENAI_API_KEY`,
          },
        }
      );

      const aiMessage = response.data.choices[0].text.trim();

      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "ai", text: aiMessage },
      ]);
    } catch (err) {
      console.error("AI API call failed", err);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "ai", text: "Sorry, something went wrong!" },
      ]);
    }

    setNewMessage("");
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleMessageSend();
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <Sidebar />
        <div className="loading-content">
          <div className="spinner"></div>
          <p>Loading your financial data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h2>Financial Dashboard</h2>
          <div className="greeting">
            <h3>Hi, {userName}! ðŸ‘‹</h3>
            <p>Here's your financial overview</p>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        {/* Stats Overview */}
        <div className="stats-grid">
          <div className="stat-card total-expenses">
            <h3>Total Expenses</h3>
            <p className="stat-amount">{formatCurrency(totalExpenses)}</p>
            <p className="stat-change">+12% from last month</p>
          </div>
          <div className="stat-card categories">
            <h3>Categories</h3>
            <p className="stat-amount">{categoryTotals.length}</p>
            <p className="stat-change">Most spent: {categoryTotals[0]?.name || 'N/A'}</p>
          </div>
          <div className="stat-card monthly">
            <h3>This Month</h3>
            <p className="stat-amount">{formatCurrency(monthlyExpenses[monthlyExpenses.length - 1]?.amount || 0)}</p>
            <p className="stat-change">Daily avg: {formatCurrency((monthlyExpenses[monthlyExpenses.length - 1]?.amount || 0) / 30)}</p>
          </div>
          <div className="stat-card largest">
            <h3>Largest Expense</h3>
            <p className="stat-amount">
              {expenses.length > 0 ? formatCurrency(Math.max(...expenses.map(e => e.amount))) : '$0.00'}
            </p>
            <p className="stat-change">
              {expenses.length > 0 ? expenses.find(e => e.amount === Math.max(...expenses.map(e => e.amount)))?.category : 'N/A'}
            </p>
          </div>
        </div>

        {/* Main Charts Section */}
        <div className="main-charts">
          {/* Monthly Trend Line Chart */}
          <div className="chart-box large">
            <h3>Monthly Spending Trend</h3>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={monthlyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Legend />
                <Line type="monotone" dataKey="amount" stroke="#8884d8" strokeWidth={2} name="Monthly Expenses" />
              </LineChart>
            </ResponsiveContainer>
          </div>

          {/* Category Distribution */}
          <div className="chart-box medium">
            <h3>Expense Distribution</h3>
            <ResponsiveContainer width="100%" height={300}>
              <RadialBarChart 
                innerRadius="20%" 
                outerRadius="100%" 
                data={categoryTotals.slice(0, 6).map((item, index) => ({
                  ...item,
                  fill: COLORS[index % COLORS.length]
                }))}
              >
                <RadialBar minAngle={15} background dataKey="value" />
                <Legend />
                <Tooltip formatter={(value) => formatCurrency(value)} />
              </RadialBarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Secondary Charts Section */}
        <div className="secondary-charts">
          {/* Daily Spending */}
          <div className="chart-box">
            <h3>Weekly Spending</h3>
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={dailyExpenses}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="day" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Bar dataKey="amount" fill="#82ca9d" name="Daily Amount" />
              </BarChart>
            </ResponsiveContainer>
          </div>

          {/* Payment Methods */}
          <div className="chart-box">
            <h3>Payment Methods</h3>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie 
                  data={paymentMethodData} 
                  cx="50%" 
                  cy="50%" 
                  outerRadius={80} 
                  dataKey="value"
                  label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                >
                  {paymentMethodData.map((entry, index) => (
                    <Cell key={index} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => formatCurrency(value)} />
              </PieChart>
            </ResponsiveContainer>
          </div>

          {/* Expense vs Category */}
          <div className="chart-box">
            <h3>Expense Distribution</h3>
            <ResponsiveContainer width="100%" height={250}>
              <ScatterChart
                margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
              >
                <CartesianGrid />
                <XAxis type="category" dataKey="name" name="Category" />
                <YAxis type="number" dataKey="value" name="Amount" />
                <Tooltip cursor={{ strokeDasharray: '3 3' }} formatter={(value) => formatCurrency(value)} />
                <Scatter name="Categories" data={categoryTotals} fill="#8884d8" />
              </ScatterChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Recent Expenses */}
        <div className="recent-expenses">
          <div className="section-header">
            <h3>Recent Transactions</h3>
            <button className="view-all">View All</button>
          </div>
          <div className="expense-table-container">
            <table className="expense-table">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Description</th>
                  <th>Category</th>
                  <th>Payment Method</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {expenses.slice(0, 5).map((expense) => (
                  <tr key={expense._id}>
                    <td>{new Date(expense.date).toLocaleDateString()}</td>
                    <td>{expense.description || 'N/A'}</td>
                    <td>
                      <span className="category-tag" style={{ backgroundColor: COLORS[categoryTotals.findIndex(c => c.name === expense.category) % COLORS.length] }}>
                        {expense.category}
                      </span>
                    </td>
                    <td>{expense.paymentMethod || 'N/A'}</td>
                    <td className={expense.amount > 100 ? 'high-expense' : ''}>
                      {formatCurrency(expense.amount)}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Chat Interface */}
      <div className="chat-icon" onClick={toggleChat}>
        <div className="chat-bubble">
          <span>ðŸ’¬</span>
          {messages.length > 0 && <span className="notification-badge">{messages.length}</span>}
        </div>
      </div>

      {chatOpen && (
        <div className="chat-panel">
          <div className="chat-header">
            <h4>Financial Assistant</h4>
            <button className="close-chat" onClick={toggleChat}>Ã—</button>
          </div>
          <div className="chat-body">
            {messages.length === 0 ? (
              <div className="welcome-message">
                <p>Hi {userName}! I'm your financial assistant. How can I help you today?</p>
                <div className="quick-questions">
                  <button onClick={() => setNewMessage("Where am I spending the most?")}>
                    Where am I spending the most?
                  </button>
                  <button onClick={() => setNewMessage("How can I reduce my expenses?")}>
                    How can I reduce my expenses?
                  </button>
                </div>
              </div>
            ) : (
              messages.map((message, index) => (
                <div
                  key={index}
                  className={`chat-message ${message.sender}`}
                >
                  <p>{message.text}</p>
                </div>
              ))
            )}
          </div>
          <div className="chat-footer">
            <input
              type="text"
              placeholder="Ask me about your finances..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyDown={handleKeyDown}
            />
            <button onClick={handleMessageSend} disabled={!newMessage.trim()}>
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
