import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Sidebar from "../components/Sidebar";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const navigate = useNavigate();
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch the user name from localStorage
  const userName = localStorage.getItem("userName") || "User";

  // Toggle Chat
  const toggleChat = () => setChatOpen(!chatOpen);

  const handleMessageSend = async () => {
    if (newMessage.trim() === "") return;

    // Add user message to the chat
    setMessages((prevMessages) => [
      ...prevMessages,
      { sender: "user", text: newMessage },
    ]);

    // Clear input field after sending
    setNewMessage("");

    // Call the AI API for a response
    try {
      setLoading(true);
      const response = await axios.post(
        "https://api.openai.com/v1/completions", // OpenAI GPT API endpoint
        {
          model: "text-davinci-003", // You can change the model type
          prompt: newMessage,
          max_tokens: 150,
          temperature: 0.7,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `AIzaSyCXAaxTAgUO51oM52xo4lHYOPAwLU0Kdow`, // Replace with your API key
          },
        }
      );

      const aiResponse = response.data.choices[0].text.trim();

      // Add AI response to the chat
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "ai", text: aiResponse },
      ]);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch AI response");
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleMessageSend();
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h2>Dashboard</h2>
          {/* Greeting Section */}
          <div className="greeting">
            <h3>Hi, BOSS!! üëã</h3>
            <p>Welcome back to your financial dashboard</p>
          </div>
        </div>

        {/* Chat Icon */}
        <div className="chat-icon" onClick={toggleChat}>
          üó®Ô∏è
        </div>

        {/* Chat Panel */}
        {chatOpen && (
          <div className="chat-panel">
            <div className="chat-header">
              <h4>Chat</h4>
              <button onClick={toggleChat}>Close</button>
            </div>
            <div className="chat-body">
              {messages.map((message, index) => (
                <div key={index} className={`chat-message ${message.sender}`}>
                  <p>{message.text}</p>
                </div>
              ))}
              {loading && <p>AI is typing...</p>}
              {error && <p className="error">{error}</p>}
            </div>
            <div className="chat-footer">
              <input
                type="text"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyDown={handleKeyDown}
              />
              <button onClick={handleMessageSend}>Send</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
